import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function StudentData() {
  const { applicationId } = useParams(); // Get the application ID from the URL
  const [studentData, setStudentData] = useState(null); // State to store student data
  const [isLoading, setIsLoading] = useState(true); // State to track loading status
  const [error, setError] = useState(null); // State to track errors

  useEffect(() => {
    // Function to fetch student data based on the application ID
    const fetchStudentData = async () => {
      try {
        const response = await fetch('http://49.206.252.212:5000/student-data', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({ applicationId })
        });
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        console.log(data);
        setStudentData(data[0]); // Access the first object in the array
        setIsLoading(false);
        console.log(studentData);
      } catch (error) {
        console.error('Error fetching student data:', error);
        setError(error);
        setIsLoading(false);
      }
    };

    // Call fetchStudentData when the component mounts and when applicationId changes
    fetchStudentData();
  }, [applicationId]);

  useEffect(() => {
    console.log('Updated studentData:', studentData);
  }, [studentData]); // Log studentData whenever it changes

  // Display loading message while fetching data
  if (isLoading) {
    return (
      <div className="container mx-auto">
        <h1 className="text-3xl font-semibold mb-4">Student Data for Application ID: {applicationId}</h1>
        <p>Loading...</p>
      </div>
    );
  }

  // Display error message if an error occurred
  if (error) {
    return (
      <div className="container mx-auto">
        <h1 className="text-3xl font-semibold mb-4">Error</h1>
        <p>{error.message}</p>
      </div>
    );
  }

  // Display student data when available
  return (
    <div className="container mx-auto">
      <h1 className="text-3xl font-semibold mb-4">Student Data for Application ID: {applicationId}</h1>
      {studentData ? (
        <div>
          {/* Render your data here */}
          <p>Name of student: {studentData.NAME}</p>
          <p>Date of birth: {studentData.NAME}</p>
          <p>Gender: {studentData.NAME}</p>
          <p>Mother Tongue: {studentData.NAME}</p>
          <p>Religion: {studentData.NAME}</p>
          <p>Cast: {studentData.NAME}</p>
          <p>Primary address: {studentData.NAME}</p>
          <p>Primary phone: {studentData.NAME}</p>
          <p>Primary country code: {studentData.NAME}</p>
          <p>Name of student: {studentData.NAME}</p>
          <p>Name of student: {studentData.NAME}</p>
          <p>Name of student: {studentData.NAME}</p>
          {/* Add other fields as needed */}
        </div>
      ) : (
        <p>No data available for application ID: {applicationId}</p>
      )}
    </div>
  );
}

export default StudentData;
