import React, { useState, useEffect } from "react";
import { PDFDocumentProxy } from "pdfjs-dist";
import { useUser } from "./contexts/context";
import { saveAs } from "file-saver";
import { PDFDocument, rgb, StandardFonts } from "pdf-lib";

function StudentForm() {
  const [data, setData] = useState({});
  const { userEmail } = useUser();

  useEffect(() => {
    // Define a function to fetch data from your backend API
    const fetchData = async () => {
      try {
        const response = await fetch(
          "http://49.206.252.212:5000/student-master-desc"
        ); // Assuming your backend server is running on the same host
        if (!response.ok) {
          throw new Error("Failed to fetch data");
        }
        const jsonData = await response.json();
        setData(jsonData);
      } catch (error) {
        console.error("Error fetching data:", error);
        // Handle error
      }
    };

    // Call the fetchData function when the component mounts
    fetchData();

    // Clean up function (optional)
    return () => {
      // Perform any necessary cleanup here
    };
  }, []); // Empty dependency array means this effect runs only once after initial render

  const [formData, setFormData] = useState({
    name: "",
    bdate: "",
    sex: "",
    mtongue: "",
    religion: "",
    caste: "",
    p_address: "",
    p_phone: "",
    distance: "",
    s_phone: "",
    mail: "",
    f_name: "",
    f_quali: "",
    f_occ: "",
    m_name: "",
    m_quali: "",
    m_occ: "",
    g_name: "",
    m_income: "",
    c_info: "",
    c_ailment: "",
    c_ailment_info: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const [file1, setFile1] = useState(null);
  const [file2, setFile2] = useState(null);
  const [file3, setFile3] = useState(null);
  const [file4, setFile4] = useState(null);
  const [file5, setFile5] = useState(null);

  const handleFileChange1 = (e) => {
    const file = e.target.files[0];
    setFile1(file);
  };

  const handleFileChange2 = (e) => {
    const file = e.target.files[0];
    setFile2(file);
  };

  const handleFileChange3 = (e) => {
    const file = e.target.files[0];
    setFile3(file);
  };

  const handleFileChange4 = (e) => {
    const file = e.target.files[0];
    setFile4(file);
  };

  const handleFileChange5 = (e) => {
    const file = e.target.files[0];
    setFile5(file);
  };

  const generatePDF = async (formData) => {
    try {
        // Create a new PDF document
        const pdfDoc = await PDFDocument.create();
        const page = pdfDoc.addPage();

        // Add content to the PDF
        const { width, height } = page.getSize();
        const fontSize = 15;
        const helveticaFont = await pdfDoc.embedFont(StandardFonts.Helvetica);

        // Title
        const title = "LILY MONTESSORI";
        const address1 =
            "House no. 1, Cheramuthu, 2nd cross, Ayappa layout,";
        const address2 = "Behind KMF, Marathahalli, Bengaluru - 560037,";
        const phone = "Phone : +9108813990";
        const email = "Email: lilymontessori2022@gmail.com";

        const titleWidth = helveticaFont.widthOfTextAtSize(title, fontSize);
        const lineHeight = fontSize + 5; // Adjust this as needed for spacing between lines
        const centerX = width / 2;

        let currentY = height - 50; // Starting y-coordinate

        // Center aligning title and address
        page.drawText(title, {
            x: centerX - titleWidth / 2, // Center aligning title
            y: currentY,
            size: fontSize + 5,
            font: helveticaFont,
            color: rgb(0, 0, 0),
        });

        currentY -= lineHeight;

        page.drawText(address1, {
            x: centerX - helveticaFont.widthOfTextAtSize(address1, fontSize) / 2, // Center aligning
            y: currentY,
            size: fontSize,
            font: helveticaFont,
            color: rgb(0, 0, 0),
        });

        currentY -= lineHeight;

        page.drawText(address2, {
            x: centerX - helveticaFont.widthOfTextAtSize(address2, fontSize) / 2, // Center aligning
            y: currentY,
            size: fontSize,
            font: helveticaFont,
            color: rgb(0, 0, 0),
        });

        currentY -= lineHeight;

        page.drawText(phone, {
            x: centerX - helveticaFont.widthOfTextAtSize(address2, fontSize) / 2, // Center aligning
            y: currentY,
            size: fontSize,
            font: helveticaFont,
            color: rgb(0, 0, 0),
        });

        currentY -= lineHeight;

        page.drawText(email, {
            x: centerX - helveticaFont.widthOfTextAtSize(address2, fontSize) / 2, // Center aligning
            y: currentY,
            size: fontSize,
            font: helveticaFont,
            color: rgb(0, 0, 0),
        });





        // Form fields
        const startX = 50;
        const startY = height - 150;

        const drawFormField = (label, value) => {
            page.drawText(label, {
                x: startX,
                y: currentY,
                size: fontSize,
                font: helveticaFont,
                color: rgb(0, 0, 0),
            });
            page.drawText(value, {
                x: startX + 200,
                y: currentY,
                size: fontSize,
                font: helveticaFont,
                color: rgb(0, 0, 0),
            });
            currentY -= lineHeight;
        };

        // Render form fields
        // drawFormField("Name:", formData.name);
        // Add other form fields here

        // Save the PDF
        const pdfBytes = await pdfDoc.save();
        const blob = new Blob([pdfBytes], { type: "application/pdf" });
        saveAs(blob, "application.pdf");
    } catch (error) {
        console.error("Error generating PDF:", error);
    }
};

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Create an object to hold the form data
      const formDataToSend = {
        ...formData, // Include regular form data properties
        files: {
          file1: await fileToBase64(file1),
          file2: await fileToBase64(file2),
          file3: await fileToBase64(file3),
          file4: await fileToBase64(file4),
          file5: await fileToBase64(file5),
        },
      };

      console.log(formDataToSend);
      // Send the form data and files separately
      const formDataResponse = await fetch(
        "http://49.206.252.212:5000/STUDENT_MASTER",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(formDataToSend),
        }
      );

      if (!formDataResponse.ok) {
        throw new Error("Network response for form data was not ok");
      }

      const formDataData = await formDataResponse.json();
      console.log("Success:", formDataData);
      if (formDataData.success) {
        alert("Data entered successfully");
        generatePDF(formData);
      } else {
        alert("Data entry failed");
      }
    } catch (error) {
      alert(error.message);
      console.error("Error:", error);
    }
  };

  const fileToBase64 = (file) => {
    return new Promise((resolve, reject) => {
      if (!file) {
        // If no file is provided, resolve the promise with a blank result
        resolve("");
        return;
      }

      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => resolve(reader.result);
      reader.onerror = (error) => reject(error);
    });
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-semibold mb-4 flex justify-center">
        Student Application Form
      </h1>
      <form className="grid grid-cols-3 gap-2" onSubmit={handleSubmit}>
        <div>
          <div className="border p-4 rounded-lg shadow-md">
            <h2 className="text-xl font-bold mb-4">Student Details</h2>
            <div className="mb-2">
              <label htmlFor="image" className="form-label block font-semibold">
                {data.IMAGE}* :
              </label>
              <input
                type="file"
                name="image"
                id="image"
                onChange={handleFileChange1}
                className="form-input"
              />
            </div>
            <div class="mb-2">
              <label for="name" class="form-label block font-semibold">
                {data.NAME}* :
              </label>
              <input
                type="text"
                name="name"
                id="name"
                value={formData.name}
                onChange={handleChange}
                class="form-input border  rounded-md px-1 py-1 mt-1 mb-1 "
              />
            </div>
            <div class="mb-2">
              <label for="dateOfBirth" class="form-label block font-semibold">
                {data.BDATE}* :
              </label>
              <input
                type="date"
                name="bdate"
                id="bdate"
                value={formData.bdate}
                onChange={handleChange}
                class="form-input px-3 py-1 mt-1 mb-1"
              />
            </div>
            <div class="mb-2">
              <label for="gender" class="form-label block font-semibold">
                {data["SEX "]}* :
              </label>
              <select
                name="sex"
                id="sex"
                value={formData.sex}
                onChange={handleChange}
                class="form-input border  rounded-md px-3 py-1 mt-1 mb-1"
              >
                <option value="">Select Gender</option>
                <option value="M">Male</option>
                <option value="F">Female</option>
              </select>
            </div>
            <div class="mb-2">
              <label for="motherTongue" class="form-label block font-semibold">
                {data.Mtongue}* :
              </label>
              <input
                type="text"
                name="mtongue"
                id="mtongue"
                value={formData.mtongue}
                onChange={handleChange}
                class="form-input border  rounded-md px-3 py-1 mt-1 mb-1"
              />
            </div>
            <div class="mb-2">
              <label for="religion" class="form-label block font-semibold">
                {data.Religion} :
              </label>
              <input
                type="text"
                name="religion"
                id="religion"
                value={formData.religion}
                onChange={handleChange}
                class="form-input border  rounded-md px-3 py-1 mt-1 mb-1"
              />
            </div>
            <div class="mb-2">
              <label for="cast" class="form-label block font-semibold">
                {data.caste} :
              </label>
              <input
                type="text"
                name="caste"
                id="caste"
                value={formData.caste}
                onChange={handleChange}
                class="form-input border  rounded-md px-3 py-1 mt-1 mb-1"
              />
            </div>
            <div class="mb-2">
              <label
                for="primaryAddress"
                class="form-label block font-semibold"
              >
                {data.P_ADDRESS}* :
              </label>
              <textarea
                name="p_address"
                id="p_address"
                value={formData.p_address}
                onChange={handleChange}
                class="form-input border rounded-md px-3 py-1 mt-1 mb-1 h-40 w-3/4"
              ></textarea>
            </div>
            <div class="mb-2">
              <label for="primaryPhone" class="form-label block font-semibold">
                {data.P_PHONE}* :
              </label>
              <input
                type="text"
                name="p_phone"
                id="p_phone"
                value={formData.p_phone}
                onChange={handleChange}
                class="form-input border  rounded-md px-3 py-1 mt-1 mb-1"
              />
            </div>
