import React, { useState, useEffect } from "react";
import Navbar from "./navbar";

function EmployeeForm() {

  const [data, setData] = useState({});

    useEffect(() => {
        // Define a function to fetch data from your backend API
        const fetchData = async () => {
            try {
                const response = await fetch('http://49.206.252.212:5000/student-master-desc'); // Assuming your backend server is running on the same host
                if (!response.ok) {
                    throw new Error('Failed to fetch data');
                }
                const jsonData = await response.json();
                setData(jsonData);
                console.log(jsonData);
            } catch (error) {
                console.error('Error fetching data:', error);
                // Handle error
            }
        };

        // Call the fetchData function when the component mounts
        fetchData();

        // Clean up function (optional)
        return () => {
            // Perform any necessary cleanup here
        };
    }, []); // Empty dependency array means this effect runs only once after initial render

  const [formData, setFormData] = useState({
    name: "",
    bdate: "",
    sex: "",
    mtongue: "",
    religion: "",
    caste: "",
    p_address: "",
    p_phone: "",
    distance: "",
    s_phone: "",
    mail: "",
    f_name: "",
    f_quali: "",
    f_occ: "",
    m_name: "",
    m_quali: "",
    m_occ: "",
    m_income: "",
    c_info: "",
    c_ailment: "",
    c_ailment_info: "",
  });

  const [file, setFile] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setFile(file);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Convert form data to JSON
    const jsonData = { ...formData }; // Copy existing form data
    const reader = new FileReader();

    if (file) {
      // If an image file is selected
      reader.readAsDataURL(file); // Read the file as base64

      reader.onload = () => {
        // When file reading is complete
        const base64URL = reader.result; // Get the base64 URL
        jsonData.imageBase64 = base64URL; // Add base64 URL to the form data
        console.log(jsonData);

        // Send JSON data to backend
        fetch("http://49.206.252.212:5000/STUDENT_MASTER", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(jsonData),
        })
          .then((response) => {
            if (!response.ok) {
              throw new Error("Network response was not ok");
            }
            return response.json();
          })
          .then((data) => {
            console.log("Success:", data);
            if (data.success) {
              alert("Data entered successfully");
            } else {
              alert(data.message);
            }
          })
          .catch((error) => {
            alert(error.message)
            console.error("Error:", error);
            // Optionally, you can handle errors here
          });
      };

      reader.onerror = (error) => {
        console.error("FileReader error:", error);
      };
    } else {
      // If no image file is selected, send only the form data
      fetch("http://49.206.252.212:5000/STUDENT_MASTER", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(jsonData),
      })
        .then((response) => {
          if (!response.ok) {
            throw new Error("Network response was not ok");
          }
          return response.json();
        })
        .then((data) => {
          console.log("Success:", data);
          if (data.success) {
            alert("Data entered successfully");
          } else {
            alert(data.message);
          }
        })
        .catch((error) => {
          alert(error.message)
          console.error("Error:", error);
          // Optionally, you can handle errors here
        });
    }
  };

  return (
    <div class="form-container max-w-lg mx-auto p-6 bg-white rounded shadow-lg">
    <h2 class="form-header text-2xl font-semibold mb-4 flex justify-center">Student Master</h2>
    <form class="form grid grid-cols-2 gap-4" onSubmit={handleSubmit}>
      <div>
        <h2 class="text-xl font-bold mb-4">Student Details</h2>
        <div class="mb-4">
          <label for="image" class="form-label block font-semibold">{data.IMAGE}* :</label>
          <input type="file" name="image" id="image" onChange={handleFileChange} class="form-input" />
        </div>
        <div class="mb-4">
          <label for="name" class="form-label block font-semibold">{data.NAME}* :</label>
          <input type="text" name="name" id="name" value={formData.name} onChange={handleChange} class="form-input border  rounded-md px-3 py-2 mt-1 mb-5 w-full" />
        </div>
        <div class="mb-4">
          <label for="dateOfBirth" class="form-label block font-semibold">{data.BDATE}* :</label>
          <input type="date" name="bdate" id="bdate" value={formData.bdate} onChange={handleChange} class="form-input px-3 py-2 mt-1 mb-5 w-full" />
        </div>
        <div class="mb-4">
          <label for="gender" class="form-label block font-semibold">{data.SEX}* :</label>
          <select name="sex" id="sex" value={formData.sex} onChange={handleChange} class="form-input border  rounded-md px-3 py-2 mt-1 mb-5 w-full">
            <option value="">Select Gender</option>
            <option value="M">Male</option>
            <option value="F">Female</option>
          </select>
        </div>
        <div class="mb-4">
          <label for="motherTongue" class="form-label block font-semibold">{data.Mtongue}* :</label>
          <input type="text" name="mtongue" id="mtongue" value={formData.mtongue} onChange={handleChange} class="form-input border  rounded-md px-3 py-2 mt-1 mb-5 w-full" />
        </div>
        <div class="mb-4">
          <label for="religion" class="form-label block font-semibold">{data.Religion} :</label>
          <input type="text" name="religion" id="religion" value={formData.religion} onChange={handleChange} class="form-input border  rounded-md px-3 py-2 mt-1 mb-5 w-full" />
        </div>
        <div class="mb-4">
          <label for="cast" class="form-label block font-semibold">{data.caste} :</label>
          <input type="text" name="caste" id="caste" value={formData.caste} onChange={handleChange} class="form-input border  rounded-md px-3 py-2 mt-1 mb-5 w-full" />
        </div>
        <div class="mb-4">
          <label for="primaryAddress" class="form-label block font-semibold">{data.P_ADDRESS}* :</label>
          <input type="text" name="p_address" id="p_address" value={formData.p_address} onChange={handleChange} class="form-input border  rounded-md px-3 py-2 mt-1 mb-5 w-full" />
        </div>
        <div class="mb-4">
          <label for="primaryPhone" class="form-label block font-semibold">{data.P_PHONE}* :</label>
          <input type="text" name="p_phone" id="p_phone" value={formData.p_phone} onChange={handleChange} class="form-input border  rounded-md px-3 py-2 mt-1 mb-5 w-full" />
        </div>
        <div class="mb-4">
          <label for="distance" class="form-label block font-semibold">{data.DISTANCE} :</label>
          <input type="text" name="distance" id="distance" value={formData.distance} onChange={handleChange} class="form-input border  rounded-md px-3 py-2 mt-1 mb-5 w-full" />
        </div>
      </div>
      <div>
        <h2 class="text-xl font-bold mb-4">Additional Details</h2>
        <div class="mb-4">
          <label for="secondaryPhone" class="form-label block font-semibold">{data.S_PHONE}:</label>

          <input type="text" name="s_phone" id="s_phone" value={formData.s_phone} onChange={handleChange} class="form-input border  rounded-md px-3 py-2 mt-1 mb-5 w-full" />
        </div>
        <div class="mb-4">
          <label for="email" class="form-label block font-semibold">{data.MAIL}* :</label>
          <input type="email" name="mail" id="mail" value={formData.mail} onChange={handleChange} class="form-input border  rounded-md px-3 py-2 mt-1 mb-5 w-full" />
        </div>
        <div class="mb-4">
          <label for="ailment" class="form-label block font-semibold">{data.C_AILMENT}?* :</label>
          <select name="c_ailment" id="c_ailment" value={formData.c_ailment} onChange={handleChange} class="form-input border  rounded-md px-3 py-2 mt-1 mb-5 w-full">
            <option value="N">No</option>
            <option value="Y">Yes</option>
          </select>
        </div>
        <div class="mb-4">
          <label for="ailmentDetails" class="form-label block font-semibold">{data.C_AILMENT_INFO} :</label>
          <input type="text" name="c_ailment_info" id="c_ailment_info" value={formData.c_ailment_info} onChange={handleChange} class="form-input border  rounded-md px-3 py-2 mt-1 mb-5 w-full" />
        </div>
      </div>
      <div>
        <h2 class="text-xl font-bold mb-4">Parent Details</h2>
        <div class="mb-4">
          <label for="fatherName" class="form-label block font-semibold">{data.F_NAME} :</label>
          <input type="text" name="f_name" id="f_name" value={formData.f_name} onChange={handleChange} class="form-input border  rounded-md px-3 py-2 mt-1 mb-5 w-full" />
        </div>
        <div class="mb-4">
          <label for="parentEducation" class="form-label block font-semibold">{data.F_QUALI} :</label>
          <input type="text" name="f_quali" id="f_quali" value={formData.f_quali} onChange={handleChange} class="form-input border rounded-md" />
        </div>
        <div class="mb-4">
          <label for="parentEducation" class="form-label block font-semibold">{data.F_OCC} :</label>
          <input type="text" name="f_occ" id="f_occ" value={formData.f_occ} onChange={handleChange} class="form-input border rounded-md" />
        </div>
        <div class="mb-4">
          <label for="motherName" class="form-label block font-semibold">{data.M_NAME}* :</label>
          <input type="text" name="m_name" id="m_name" value={formData.m_name} onChange={handleChange} class="form-input border  rounded-md px-3 py-2 mt-1 mb-5 w-full" />
        </div>
        <div class="mb-4">
          <label for="motherName" class="form-label block font-semibold">{data.M_QUALI}* :</label>
          <input type="text" name="m_quali" id="m_quali" value={formData.m_quali} onChange={handleChange} class="form-input border  rounded-md px-3 py-2 mt-1 mb-5 w-full" />
        </div>
        <div class="mb-4">
          <label for="motherName" class="form-label block font-semibold">{data.M_OCC}* :</label>
          <input type="text" name="m_occ" id="m_occ" value={formData.m_occ} onChange={handleChange} class="form-input border  rounded-md px-3 py-2 mt-1 mb-5 w-full" />
        </div>
        <div class="mb-4">
          <label for="guardianName" class="form-label block font-semibold">{data.G_NAME} :</label>
          <input type="text" name="g_name" id="g_name" value={formData.g_name} onChange={handleChange} class="form-input border  rounded-md px-3 py-2 mt-1 mb-5 w-full" />
        </div>
        <div class="mb-4">
          <label for="monthlyIncome" class="form-label block font-semibold">{data.M_INCOME} :</label>
          <input type="text" name="m_income" id="m_income" value={formData.m_income} onChange={handleChange} class="form-input border  rounded-md px-3 py-2 mt-1 mb-5 w-full" />
        </div>
        <div class="mb-4">
          <label for="otherDetails" class="form-label block font-semibold">{data.C_INFO} :</label>
          <input type="text" name="C_INFO" id="c_info" value={formData.c_info} onChange={handleChange} class="form-input border rounded-md px-3 py-2 mt-1 mb-5 w-full" />
        </div>
      </div>
      <div class="col-span-2 flex justify-center mt-6">
        <button type="submit" class="form-button bg-indigo-600 hover:bg-indigo-700 text-white font-bold py-2 px-12 rounded px-3 py-2 mt-1 mb-5 w-full">Submit</button>
      </div>
    </form>
  </div>


  );
}

export default EmployeeForm;
