import React, { useState, useEffect } from 'react';
import { Link } from "react-router-dom";

function ListAccepted() {
    const [applications, setApplications] = useState([]);
    const [registration, setRegistration] = useState([]);

    // Fetch application IDs and registration status from backend when component mounts
    useEffect(() => {
      fetchApplications();
      fetchRegistration();
    }, []);

    // Function to fetch application IDs from backend
    const fetchApplications = async () => {
      try {
        const response = await fetch('http://49.206.252.212:5000/accepted-ids');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setApplications(data);
      } catch (error) {
        console.error('Error fetching application IDs:', error);
      }
    };

    // Function to fetch registration status from backend
    const fetchRegistration = async () => {
      try {
        const response = await fetch('http://49.206.252.212:5000/registration-status');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setRegistration(data);
      } catch (error) {
        console.error('Error fetching registration status:', error);
      }
    };

    // Function to check if registration is complete for an application
    const isRegistrationComplete = (applicationId) => {
      const registrationInfo = registration.find(item => item.student_id === applicationId);
      return registrationInfo && registrationInfo.fee_reg;
    };

  return (
    <div className="container mx-auto">
      <h1 className="text-3xl font-semibold mb-4 flex justify-center">New Applications</h1>
      <table className="table-auto mx-auto">
        <thead>
          <tr>
            <th className="border px-9 py-2">Application ID</th>
            <th className="border px-9 py-2">Fee registration</th>
            <th className="border px-9 py-2">Attendance registration</th>
            <th className="border px-9 py-2">Sibling registration</th>
          </tr>
        </thead>
        <tbody>
          {applications.map(application => (
            <tr key={application.application_id}>
              <td className="border px-4 py-2">{application.application_id}</td>
              <td className="border px-4 py-2">
                {isRegistrationComplete(application.student_id) ? 
                  "Registered" : 
                  <Link to={`/fee-register/${application.application_id}`} className="flex justify-center bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
                    Register
                  </Link>
                }
              </td>
              <td className="border px-4 py-2">
                <Link to={`/student-data/${application.application_id}`} className="flex justify-center bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
                  Register
                </Link>
              </td>
              <td className="border px-4 py-2">
                <Link to={`/student-data/${application.application_id}`} className="flex justify-center bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
                  Register
                </Link>
              </td>
            </tr>
          ))}
          {applications.length === 0 && (
            <tr>
              <td className="border px-4 py-2" colSpan="2">No unregistered applications</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}

export default ListAccepted;
