import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

function Login() {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    email: "",
    password: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Convert form data to JSON
    const jsonData = JSON.stringify(formData);

    // Send JSON data to backend
    fetch("http://49.206.252.212:5000/login", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: jsonData,
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => {
        if (data.success) {
          alert("Login successful");
          // Redirect to home page
          navigate("/home");
        } else {
          alert("Not authorised");
        }
      })
      .catch((error) => {
        console.error("Error:", error);
        // Optionally, you can handle errors here
      });
  };

  return (
    <div className="bg-white h-screen flex justify-center items-center">
      <div className="bg-gray-800 text-white p-8 rounded-lg w-2/3 max-w-2xl">
        <center>
          <h1 className="text-red mb-8">Admin Login</h1>
        </center>
        <form onSubmit={handleSubmit}>
          <h2 className="text-center mb-8">Login</h2>
          <label className="block mb-4">
            Email:
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              className="block w-full px-4 py-2 mt-2 border border-gray-600 rounded bg-gray-700 text-white"
            />
          </label>
          <label className="block mb-4">
            Password:
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              className="block w-full px-4 py-2 mt-2 border border-gray-600 rounded bg-gray-700 text-white"
            />
          </label>
          <button
            type="submit"
            className="block w-full px-4 py-2 mt-4 bg-white text-black rounded hover:bg-gray-400"
          >
            Login
          </button>
        </form>
      </div>
    </div>
  );
}

export default Login;
